package main

import (
	"crypto/sha1"
	"crypto/sha256"
	"crypto/sha512"
	"hash"
	// "log"
	"testing"
)

func TestPlaceholder(t *testing.T) {
	// do nothing.
}

func runHasher(b *testing.B, hasher hash.Hash) {
	inputs := [][]byte{
		// []byte("a"),
		// []byte("abcdefghijklmnopqrztuvwxyz012345"),
		// []byte("abcdefghijklmnopqrztuvwxyz012345abcdefghijklmnopqrztuvwxyz012345abcdefghijklmnopqrztuvwxyz012345abcdefghijklmnopqrztuvwxyz012345"),
		[]byte(""),
		[]byte(""),
	}

	for i := 0; i < b.N; i++ {
		for _, input := range inputs {
			hasher.Write(input)
			hasher.Sum(nil)
		}
	}
}

func BenchmarkSHA1(b *testing.B) {
	runHasher(b, sha1.New())
}
func BenchmarkSHA224(b *testing.B) {
	runHasher(b, sha256.New224())
}
func BenchmarkSHA256(b *testing.B) {
	runHasher(b, sha256.New())
}
func BenchmarkSHA384(b *testing.B) {
	runHasher(b, sha512.New384())
}
func BenchmarkSHA512(b *testing.B) {
	runHasher(b, sha512.New())
}

